    cities: [
      { id: uuid(), name: 'Chelsea'},
      { id: uuid(), name: 'Greenpoint'},
      { id: uuid(), name: 'Bushwick'},
      { id: uuid(), name: 'LIC'},
    ]
  }
  render() {
    const { cities } = this.state;
    return (
      <Container>
        <Button 
