              }));
            }
          }}
        >Add City</Button>

        <input type="text" value ={this.state.search} onChange={this.updateSearch.bind(this)}></input>

        <ListGroup>
          {this.state.loading ? <p>loading...</p> : 
          <TransitionGroup className="cities-list">
            {this.state.cities.map(({ id, name, country }) => (
              <CSSTransition key={id} timeout={500} classNames="fade">
                <ListGroupItem>
                  <p>{name}</p><p>{country}</p>
                </ListGroupItem>
              </CSSTransition>
            ))}
          </TransitionGroup>
          }
        </ListGroup>
      </Container>
    );
  }
}
