import React, { Component } from 'react';
import { Nav, NavItem, Container } from 'reactstrap';
import { connect } from 'react-redux'

//components
import NavBar from './NavBar.js'

//action imports
import { } from '../store/actions/cityActions';

class MyProfile extends Component {


    render() {
        return(
            <div>
                <NavBar></NavBar>
                <Container>
                    <h2> 
                        <img src="https://img.icons8.com/ios/50/000000/salute.png"></img>
                        Welcome {this.props.currentUsername}!
                    </h2>
                </Container>
                <Container>
                    <h4>Info:</h4>
                    <div>
                    <b>Profile Picture:</b> <img src={this.props.currentProfPicUrl} alt="profile" id="profileCompPic"></img>

                        <b>Display Name:</b> {this.props.currentUsername} <br></br>
                        <b>Email:</b> {this.props.currentUsername} <br></br>
                    </div>
                </Container>
                <Container>
                    <h4>Favorites:</h4>
                    <Nav tabs>
                        <NavItem active>
                            Activities
                        </NavItem>
                        <NavItem>
                            Itineraries
                        </NavItem>
                    </Nav>
                </Container>
            </div>
        )
    }

}

const mapStateToProps = (state) => {
    return {
        currentUsername: state.cities.currentUsername,
        currentProfPicUrl: state.cities.currentProfPicUrl,
    }
  }

export default connect(mapStateToProps)(MyProfile)
