import React, { Component } from 'react';
import { Container, ListGroup, ListGroupItem } from 'reactstrap';
import { connect } from 'react-redux'
//components
import ItineraryCover from './ItineraryCover.js'


class PopularItineraries extends Component {

    render() {
        const cityCovers = []

        for (let i=0;i<this.props.cities.length;i++) {
            cityCovers.push(
                <ItineraryCover
                imageUrl = {this.props.cities[i].imageUrl}
                styleInfo={"PopularItineraries"}
                cityName={this.props.cities[i]}
                ></ItineraryCover>
            )
        }

        return(
            <Container>
                <ItineraryCover
                imageUrl = {'https://www.minneapolis.org/imager/s3_amazonaws_com/meet-minneapolis/craft/cms/meta-images/Homepage-Hero-Meta-Image-WebsiteImagery_1920x1080_SkylineDrone_GianLorenzo_d6fd2276c2892c7bc12b1e84a87bc8f5.jpg'}
                styleInfo={"PopularItineraries"}
                cityName={'Minneapolis'}
                ></ItineraryCover>
            </Container>
        )
    }

}

const mapStateToProps = (state) => {
    return {
      cities: state.cities.cities,
    }
  }

export default connect()(PopularItineraries)
