import React, { Component } from 'react';
import { Nav, NavItem, NavLink, Container } from 'reactstrap';
import { connect } from 'react-redux'

//components
import NavBar from './NavBar.js'

//action imports
import { } from '../store/actions/cityActions';

class MyProfile extends Component {

    switchFavorite() {
        console.log('hi')
    }


    render() {
        return(
            <div>
                <NavBar></NavBar>
                <Container>
                    <h2> 
                        <img src="https://img.icons8.com/ios/50/000000/salute.png"></img>
                        Welcome lalalalallalala{this.props.currentUsername}!
                    </h2>
                </Container>
                <Container className="infoContainers">
                    <h4>Info:</h4>
                    <div id="profileInfoContainer"> 
                        <div id="nameAndEmail">
                            <b>Picture:</b><br></br> 
                            <img src={this.props.currentProfPicUrl} alt="profile" id="profileCompPic"></img>
                        </div>
                        <div>
                            <b>Display Name:</b> {this.props.currentUsername} <br></br>
                            <b>Email:</b> lalallalala{this.props.currentUsername} <br></br>
                        </div>

                    </div>
                </Container>
                <Container className="infoContainers">
                    <h4>Favorites:</h4>
                    <Nav tabs id="favoritesNav">
                        <NavItem 
                        onClick= {()=> {
                            this.switchFavorite(1)
                        }}
                        className={classnames({active: activeTab === 1})}>
                            <NavLink>
                                Activities
                            </NavLink>
                        </NavItem>
                        <NavItem 
                        onClick= {()=> {
                            this.switchFavorite(2)
                        }}
                        className={classnames({active: activeTab === 2})}>
                            <NavLink>
                                Itineraries
                            </NavLink>
                        </NavItem>
                    </Nav>
                </Container>
            </div>
        )
    }

}

const mapStateToProps = (state) => {
    return {
        currentUsername: state.cities.currentUsername,
        currentProfPicUrl: state.cities.currentProfPicUrl,
    }
  }

export default connect(mapStateToProps)(MyProfile)
