const fetchCitiesAction = {type: 'FETCH_CITIES_REQUEST', response: "request being made."}
const fetchCitiesActionSuccess = {type: 'FETCH_CITIES_REQUEST', response: "something"}
const fetchCitiesActionFailure = {type: 'FETCH_CITIES_REQUEST', error: "Error message - request for cities failed"}

export const RECEIVE_CITIES = 'RECEIVE_POSTS'

function receiveCities(json) {
  return {
    type: RECEIVE_CITIES,
    cities: json.data.children.map(child => child.data),
    receivedAt: Date.now()
  }
}
