import React, { Component } from 'react';
import { Container, ListGroup, ListGroupItem, Button } from 'reactstrap';
import { CSSTransition, TransitionGroup } from 'react-transition-group';
import { connect } from 'react-redux'

//components
import PostResource from './PostResource';

//action imports
import { filterCities } from '../store/actions/cityActions';

class Cities extends Component {
  constructor(props) {
    super(props)

    this.clearFields = this.clearFields.bind(this);

  }

  state = {
    search: "",
  }

  async componentDidMount() {
    console.log(this.props.loading)
    // this.timer = setInterval(()=> console.log('ok'), 5000)
  }

  async updateSearch(e) {
    await this.setState({search: e.target.value});
    const searchResults = this.props.cities.filter( (city) =>{
      return city.name.indexOf(this.state.search) !== -1;
    })
    this.props.dispatch(filterCities(searchResults))
  }

  async updateNewCity(e) {
    let newArrary = []
    for(let i =0; i<this.props.cities.length; i++) {
      newArrary.push(this.props.cities[i].name)
    }
    if(!newArrary.includes(e.target.value)) {
      await this.setState({newCity: e.target.value});
    } else {
      console.log('Sorry! This city was already created')
    }
  }

  async updateNewCountry(e) {
    //dispatch to set the state var to this and set e.target.value back to "" for all
    await this.setState({newCountry: e.target.value});
  }

  async updateNewUrl(e) {
    await this.setState({newUrl: e.target.value});
  }

  clearFields() {
    console.log('here')
    this.setState({newCity: ""});
    this.setState({newCountry: ""});
    this.setState({newUrl: ""});
  }


  render() {
    return (
      <Container>

        <h1>Cities</h1>

        <label htmlFor="searchBar"> Add City: </label>
        <input id="searchBar" type="text" value ={this.state.newCity} onChange={this.updateNewCity.bind(this)}></input>
        <br></br> <label htmlFor="searchBar"> Country: </label>
        <input id="searchBar" type="text" value ={this.state.newCountry} onChange={this.updateNewCountry.bind(this)}></input>
        <br></br><label htmlFor="searchBar"> Image URL: </label>
        <input id="searchBar" type="text" value ={this.state.newUrl} onChange={this.updateNewUrl.bind(this)}></input>
        <br></br>

        <PostResource 
          url="http://localhost:5000/api/cities" 
          //Perhaps take this from 
          dataObject={{"name": this.state.newCity, "country": this.state.newCountry, "imageUrl": this.state.newUrl}} 
          action={this.clearFields}
          >  
        </PostResource>

        <br></br>
        <label htmlFor="searchBar"> Search List: </label>
        <input id="searchBar" type="text" value ={this.state.search} onChange={this.updateSearch.bind(this)}></input>
        <Button color="dark" 
            // onClick={()=> {}
            > Search</Button>

        <ListGroup>
          {this.props.loading ? <p>loading...</p> : 
          <TransitionGroup className="cities-list">
            {this.props.filteredResults.map(({ id, name, country, }) => (
              <CSSTransition key={id} timeout={500} classNames="fade">
                <ListGroupItem>
                  <p>{name}</p><p>{country}</p>
                </ListGroupItem>
              </CSSTransition>
            ))}
          </TransitionGroup>
          }
        </ListGroup>
      </Container>
    );
  }

}

const mapStateToProps = (state) => {
  return {
    cities: state.cities.cities,
    status: state.cities.status,
    loading: state.cities.loading,
    filteredResults: state.cities.filteredResults,
  }
}

// const mapDispatchToProps = dispatch => {
//   return {
//     filterSearch: searchResults => dispatch(filterCities(searchResults))
//   }
// }


export default connect(mapStateToProps)(Cities)
