import React, { Component } from 'react';
import MYtineraryLogo from '../assets/MYtineraryLogo.png'
import '../assets/LandingPage.css';
import jwt_decode from 'jwt-decode' // import dependency
import { connect } from 'react-redux'


//Components
import StartBrowsing from './StartBrowsing'
import NavBar from './NavBar'
import PopularItineraries from './PopularItineraries'
//on component did mount (so it happens only once bc you created an infite redirect earlier)
//  add this stuff below and also change the url to not have the hash in it and check that it still 
//  is stored in local storage

// componentDidMount() {
