//components
import NavBar from './NavBar.js'
import PostResource from './PostResource.js';

//action imports
import { setEmail, setPassword, } from '../store/actions/cityActions';

class LogIn extends Component {

    async updateEmail(e) {
        this.props.dispatch(setEmail(e.target.value))
    }

    async updatePassword(e) {
        this.props.dispatch(setPassword(e.target.value))
    }

    getGoogleConsent() {
        window.location.href = "http://localhost:5000/api/users/google"
    }

    componentToggle(num) {
        if (num === 0) {
            console.log('Back')
        } else if (num ===1) {
            console.log('Google')
            //this should be getGoogleConsent()
        } else if (num ===2) {
            console.log('Email')
        } else if (num ===3) {
            console.log('Create Account')
        }
    }

    render() {
        return(
            <div>
                <NavBar></NavBar>
                <Container className="logInHeader">
                    <img src="https://img.icons8.com/android/24/000000/user.png"></img>
                    <h2>Log In</h2>
                </Container>
                <Container id="signInButtonsContainer">
                {/* <Form>
                    <FormGroup>
                        <Label for="email">Email</Label>
