                        <Input type="password" name="password" id="password" placeholder="" value ={this.props.password} onChange={this.updatePassword.bind(this)} />
                        <FormText color="muted">
                            Passwords must be no less than 8 characters.
                        </FormText>
                    </FormGroup>
                    <PostResource
                        url="http://localhost:5000/api/users/login"
                        parentComp = "logIn"
                        >
                    </PostResource>
                </Form> */}
                <Button id ="signInWithGoogle"
                onClick={()=> {
                    // this.getGoogleConsent()
                    this.componentToggle(1)
                }}
                >
                    <img src="https://img.icons8.com/color/48/000000/google-logo.png"></img>
                    Log in with Google
                </Button>
                <Button id="signInWithEmail"
                onClick={()=> {
                    // this.getGoogleConsent()
                    this.componentToggle(2)
                }}
                >
                    <img src="https://img.icons8.com/color/24/000000/secured-letter.png"></img>
                    Log in with email
                </Button>
                <Button id="createAccountWithEmail"
                onClick={()=> {
                    // this.getGoogleConsent()
                    this.componentToggle(3)
                }}
                >
                    <img src="https://img.icons8.com/android/24/000000/add-user-male.png"></img>
                    Create new account with email
                </Button>
                {/* <CSSTransition
                in={this.state.transish}
                timeout={500}
