                unmountOnExit
                >
                <div>
                Hello hi
                </div>
                </CSSTransition> */}
                </Container>
            </div>
        )
    }

}

const mapStateToProps = (state) => {
    return {
        email: state.cities.email,
        password: state.cities.password,
    }
  }

export default connect(mapStateToProps)(LogIn)
