export function getCities() {
    return(dispatch)=> {
        return fetch("http://localhost:5000/api/cities").then((response)=> {
            const data = response.json()
            console.log(data)
            dispatch(fetchCitiesAction(data))
        })
        .catch((error)=> {
            dispatch(fetchCitiesErrorAction(error))
        })
    }
  }

export function fetchCitiesAction(data) {
    return {
        type: 'FETCH_CITIES_REQUEST', 
        status: '200 OK', 
        response: data,
    }
}

export function fetchCitiesErrorAction(error) {
    return {
        type: 'FETCH_CITIES_ERROR', 
        status: error, 
        response: "",
    }
}
